mapper.select:

SELECT product_id, logtime, avg(price) AS price
FROM priceConfirmed
WHERE product_id = #{product_id}
   AND logtime < #{today}
   AND logtime >= DATE_SUB(#{today}, INTERVAL 7 DAY)
GROUP BY logtime

select product_size, MIN(bidding_price) AS bidding_price, pid
from biddings
join product using(pid)
where product_size=#{size} AND  status="입찰중" and pid=#{pid}
GROUP BY product_size 
ORDER BY product_size ASC;
